	public CustomizeBill createCustomizeBill(String billXml, String paramString) throws InterfaceException, Exception {

		CustomizeBill customizeBill = null;
		try {

			Node svcCont = InterfaceClient.checkCSBXml(PortalServiceCode.GET_CUSTOMIZE_BILL_DATA, billXml, paramString);
			String svcXml = svcCont.asXML();
			Document svcDom = XmlUtil.createw3cDocument(svcXml);
			Map<String, Object> svcContMap = new HashMap<String, Object>();
			XmlUtil.xmlConversionObject(svcDom, svcContMap);
			svcContMap = MapUtils.getMap(svcContMap, "SvcCont");
			Map<String, Object> tmpMap = MapUtils.getMap(svcContMap, "CustomizeBillPrintBillRsp");
			int serviceResultCode = MapUtils.getIntValue(tmpMap, "Service_Result_Code", 1);
			customizeBill = new CustomizeBill();
			customizeBill.setServiceResultCode(serviceResultCode);
			customizeBill.setParaFieldResult(MapUtils.getString(tmpMap, "Para_Field_Result", "计费未返回错误信息, Para_Field_Result节点为空"));
			tmpMap = MapUtils.getMap(tmpMap, "bill_format_Info");
			//结果不成功，或者没有账单信息，则应该返回
			if (serviceResultCode != 0 || MapUtils.isEmpty(tmpMap)) {
				return customizeBill;
			}
			BillFormatInfo billFormatInfo = new BillFormatInfo();
			billFormatInfo.setPaperWidth(MapUtils.getIntValue(tmpMap, "paper_width"));
			billFormatInfo.setPaperHeight(MapUtils.getIntValue(tmpMap, "paper_height"));
			List<Map<String, Object>> tmpSheetInfoList = returnListIncaseOfMap(tmpMap, "sheet_info");
			List<SheetInfo> sheetInfoList = new ArrayList<SheetInfo>();
			for (int sheetInfoIndex = 0; tmpSheetInfoList != null && sheetInfoIndex < tmpSheetInfoList.size(); sheetInfoIndex++) {
				SheetInfo sheetInfo = new SheetInfo();
				Map<String, Object> tmpSheetInfoMap = tmpSheetInfoList.get(sheetInfoIndex);
				sheetInfo.setPage(MapUtils.getIntValue(tmpSheetInfoMap, "page"));
				List<Map<String, Object>> tmpBlockInfoList = returnListIncaseOfMap(tmpSheetInfoMap, "block_info");
				List<BlockInfo> blockInfoList = new ArrayList<BlockInfo>();
				for (int blockInfoIndex = 0; tmpBlockInfoList != null && blockInfoIndex < tmpBlockInfoList.size(); blockInfoIndex++) {
					BlockInfo blockInfo = new BlockInfo();
					Map<String, Object> tmpBlockInfoMap = tmpBlockInfoList.get(blockInfoIndex);
					blockInfo.setLocationX(MapUtils.getIntValue(tmpBlockInfoMap, "location_x"));
					blockInfo.setLocationY(MapUtils.getIntValue(tmpBlockInfoMap, "location_y"));
					blockInfo.setBlockHeight(MapUtils.getIntValue(tmpBlockInfoMap, "block_height"));
					blockInfo.setBlockLength(MapUtils.getIntValue(tmpBlockInfoMap, "block_length"));
					int blockType = MapUtils.getIntValue(tmpBlockInfoMap, "block_type");
					blockInfo.setBlockType(blockType);
					if (blockType == 1) {
						Table table = new Table();
						Map<String, Object> tmpTableMap = MapUtils.getMap(tmpBlockInfoMap, "table");
						Map<String, Object> tmpTableFormatMap = MapUtils.getMap(tmpTableMap, "table_format");
						TableFormat tableFormat = new TableFormat();
						tableFormat.setCellLeftSpace(MapUtils.getIntValue(tmpTableFormatMap, "cell_left_space"));
						tableFormat.setCellRightSpace(MapUtils.getIntValue(tmpTableFormatMap, "cell_right_space"));
						String column = MapUtils.getString(tmpTableFormatMap, "column", "");
						if (StringUtils.isBlank(column)) {
							logger.error("客户化账单打印时，table_format中的column为空");
							throw new InterfaceException(ErrType.CATCH, PortalServiceCode.GET_CUSTOMIZE_BILL_DATA, "客户化账单打印时，table_format中的column为空", "980001", billXml, "");
						}
						if ("0".equals(column)) {
							logger.error("客户化账单打印时，table_format中的column为0");
							throw new InterfaceException(ErrType.CATCH, PortalServiceCode.GET_CUSTOMIZE_BILL_DATA, "客户化账单打印时，table_format中的column为0", "980001", billXml, "");
						}
						tableFormat.setColumn(Integer.parseInt(column));
						tableFormat.setDefaultColumnWidth(MapUtils.getIntValue(tmpTableFormatMap, "default_column_width"));
						tableFormat.setDefaultRowHeight(MapUtils.getIntValue(tmpTableFormatMap, "default_row_height"));
						tableFormat.setFrame(MapUtils.getIntValue(tmpTableFormatMap, "frame"));
						String row = MapUtils.getString(tmpTableFormatMap, "row", "");
						if (StringUtils.isBlank(row)) {
							logger.error("客户化账单打印时，table_format中的row为空");
							throw new InterfaceException(ErrType.CATCH, PortalServiceCode.GET_CUSTOMIZE_BILL_DATA, "客户化账单打印时，table_format中的row为空", "980001", billXml, "");
						}
						if ("0".equals(row)) {
							logger.error("客户化账单打印时，table_format中的row为0");
							throw new InterfaceException(ErrType.CATCH, PortalServiceCode.GET_CUSTOMIZE_BILL_DATA, "客户化账单打印时，table_format中的row为0", "980001", billXml, "");
						}
						tableFormat.setRow(Integer.parseInt(row));
						
						table.setTableFormat(tableFormat);
						
						if (tmpTableMap.containsKey("row_define")) {
							List<Map<String, Object>> tmpRowDefineList = returnListIncaseOfMap(tmpTableMap, "row_define");
							List<RowDefine> rowDefineList = new ArrayList<RowDefine>();
							for (int rowDefineIndex = 0; tmpRowDefineList != null && rowDefineIndex < tmpRowDefineList.size(); rowDefineIndex++) {
								Map<String, Object> tmpRowDefineMap = tmpRowDefineList.get(rowDefineIndex);
								RowDefine rowDefine = new RowDefine();
								rowDefine.setRowHeight(MapUtils.getIntValue(tmpRowDefineMap, "row_height"));
								rowDefine.setRowId(MapUtils.getIntValue(tmpRowDefineMap, "row_id"));
								
								rowDefineList.add(rowDefine);
							}
							
							table.setRowDefineList(rowDefineList);
						}
						
						//协议中为colume_define，需注意
						if (tmpTableMap.containsKey("colume_define")) {
							List<Map<String, Object>> tmpColumnDefineList = returnListIncaseOfMap(tmpTableMap, "colume_define");
							List<ColumnDefine> columnDefineList = new ArrayList<ColumnDefine>();
							for (int columnDefineIndex = 0; tmpColumnDefineList != null && columnDefineIndex < tmpColumnDefineList.size(); columnDefineIndex++) {
								Map<String, Object> tmpColumnDefineMap = tmpColumnDefineList.get(columnDefineIndex);
								ColumnDefine columnDefine = new ColumnDefine();
								columnDefine.setColumnId(MapUtils.getIntValue(tmpColumnDefineMap, "column_id"));
								columnDefine.setColumnWidth(MapUtils.getIntValue(tmpColumnDefineMap, "column_width"));
								
								columnDefineList.add(columnDefine);
							}
							table.setColumnDefineList(columnDefineList);
						}
						
						List<Map<String, Object>> tmpCellInfoList = returnListIncaseOfMap(tmpTableMap, "cell_info");
						List<CellInfo> cellInfoList = new ArrayList<CellInfo>();
						for (int cellInfoIndex = 0; tmpCellInfoList != null && cellInfoIndex < tmpCellInfoList.size(); cellInfoIndex++) {
							Map<String, Object> tmpCellInfoMap = tmpCellInfoList.get(cellInfoIndex);
							CellInfo cellInfo = new CellInfo();
							cellInfo.setBottomColour(MapUtils.getString(tmpCellInfoMap, "bottom_colour"));
							cellInfo.setCellColumnId(MapUtils.getIntValue(tmpCellInfoMap, "cell_column_id"));
							cellInfo.setCellContent(MapUtils.getString(tmpCellInfoMap, "cell_content"));
							cellInfo.setCellRowId(MapUtils.getIntValue(tmpCellInfoMap, "cell_row_id"));
							
							AlignmentInfo alignmentInfo = new AlignmentInfo();
							Map<String, Object> tmpAlignmentInfoMap = MapUtils.getMap(tmpCellInfoMap, "alignment_info");
							alignmentInfo.setAlignmentHorizontal(MapUtils.getIntValue(tmpAlignmentInfoMap, "alignment_horizontal"));
							alignmentInfo.setAlignmentVertical(MapUtils.getIntValue(tmpAlignmentInfoMap, "alignment_vertical"));
							cellInfo.setAlignmentInfo(alignmentInfo);
							
							FontInfo fontInfo = new FontInfo();
							Map<String, Object> tmpFontInfoMap = MapUtils.getMap(tmpCellInfoMap, "font_info");
							fontInfo.setFontColour(MapUtils.getString(tmpFontInfoMap, "font_colour"));
							fontInfo.setFontSize(MapUtils.getString(tmpFontInfoMap, "font_size"));
							fontInfo.setFontStyle(MapUtils.getIntValue(tmpFontInfoMap, "font_style"));
							fontInfo.setFontType(MapUtils.getIntValue(tmpFontInfoMap, "font_type"));
							fontInfo.setFontUnderline(MapUtils.getIntValue(tmpFontInfoMap, "font_underline"));
							fontInfo.setIndent(MapUtils.getIntValue(tmpFontInfoMap, "indent"));
							cellInfo.setFontInfo(fontInfo);
							
							cellInfoList.add(cellInfo);
						}
						table.setCellInfoList(cellInfoList);
						
						
						blockInfo.setTable(table);
					} else if (blockType == 2) {
						PictureInfo pictureInfo = new PictureInfo();
						Map<String, Object> tmpPictureInfoMap = MapUtils.getMap(tmpBlockInfoMap, "picture_info");
						pictureInfo.setDisplay(MapUtils.getIntValue(tmpPictureInfoMap, "display"));
						pictureInfo.setFileName(MapUtils.getIntValue(tmpPictureInfoMap, "file_name"));
						
						blockInfo.setPictureInfo(pictureInfo);
					} else if (blockType == 3) {
						HistogramInfo histogramInfo = new HistogramInfo();
						Map<String, Object> tmpHIMap = MapUtils.getMap(tmpBlockInfoMap, "histogram_info");
						histogramInfo.setGraphColumnWidth(MapUtils.getIntValue(tmpHIMap, "graph_column_width"));
						histogramInfo.setIfDisplaValue(MapUtils.getIntValue(tmpHIMap, "if_displa_value"));
						List<HistogramDataInfo> HDIList = new ArrayList<HistogramDataInfo>();
						List<Map<String, Object>> tmpHDIList = returnListIncaseOfMap(tmpHIMap, "histogram_datainfo");
						for (int HDIIndex = 0; tmpHDIList != null && HDIIndex < tmpHDIList.size(); HDIIndex++) {
							HistogramDataInfo hdi = new HistogramDataInfo();
							Map<String, Object> tmpHDIMap = tmpHDIList.get(HDIIndex);
							hdi.setxValue(MapUtils.getIntValue(tmpHDIMap, "x_value"));
							hdi.setyValue(MapUtils.getIntValue(tmpHDIMap, "y_value"));
							hdi.setGraphColour(MapUtils.getString(tmpHDIMap, "graph_colour"));
							
							HDIList.add(hdi);
						}
						
						histogramInfo.setHistogramDataInfoList(HDIList);
						
						blockInfo.setHistogramInfo(histogramInfo);
					}
					blockInfoList.add(blockInfo);
				}
				
				sheetInfo.setBlockInfoList(blockInfoList);
				
				sheetInfoList.add(sheetInfo);
			}
			
			billFormatInfo.setSheetInfoList(sheetInfoList);
			
			customizeBill.setBillFormatInfo(billFormatInfo);
			
//			System.out.println(docMap);
//			logger.info("document.toString():" + document.toString());
//			customizeBill = ParseXML.parseCustomizeBillPrintBill1ByDocument(document);
		} catch (InterfaceException ie) {
			throw ie;
		} catch (Exception e) {
			logger.info("Parse xml exception:", e);
			throw e;
		}

		return customizeBill;
	}

	/**
	 * 想取的对象可能是 Map 也可能是 List，统一返回List
	 * @param map
	 * @param key
	 * @return
	 */
	private List<Map<String, Object>> returnListIncaseOfMap(Map<String, Object> map, String key) {
		Object obj = map.get(key);
		List<Map<String, Object>> list = null;
		if (obj instanceof List) {
			list = (List<Map<String, Object>>) obj;
		} else if (obj instanceof Map) {
			list = new ArrayList<Map<String,Object>>();
			list.add((Map<String, Object>) obj);
		}
		return list;
	}
	
