	/**
	 * 创建HTML文件
	 * 
	 * @param customizeBill
	 */
	public String createHtml(CustomizeBill customizeBill) {
		
		if (customizeBill == null) {
			logger.error("不存在客户化帐单打印返回的数据内容!");
			return "";
		}
		
		StringBuffer htmlSb = new StringBuffer();
		String picturePath = "../image/common/brand/";// 图片路径
		try {
			// 帐单类信息组
			BillFormatInfo billFormatInfo = customizeBill.getBillFormatInfo();
			// 纸张宽度（单位：毫米）
			int paperWidthInt = billFormatInfo.getPaperWidth();
			// 纸张高度（单位：毫米）
			int paperHeightInt = billFormatInfo.getPaperHeight();

			List<SheetInfo> sheetInfoList = billFormatInfo.getSheetInfoList();
			
			int iAbsolute_y = 0;

			// 页列表
			for (SheetInfo sheetInfo : sheetInfoList) {
				// 页码
				int sheetPageInt = sheetInfo.getPage();
				List<BlockInfo> blockInfoList = sheetInfo.getBlockInfoList();

				// 块列表
				out: for (BlockInfo bolckBlockInfo : blockInfoList) {
					// 起始位置横坐标
					float locationXFloat = bolckBlockInfo.getLocationX();
					// 起始位置纵坐标
					float locationYFloat = bolckBlockInfo.getLocationY();
					// 块高度
					float blockHeighFloat = getValueWithDefault(bolckBlockInfo.getBlockHeight(), 100);
					// 块长度
					float blockLengthFloat = getValueWithDefault(bolckBlockInfo.getBlockLength(), 100);
					// 块分类
					int blockType = bolckBlockInfo.getBlockType();

					// 表格
					if (blockType == 1) {
						// 表格
						Table table = bolckBlockInfo.getTable();
						// 表格格式
						TableFormat tableFormat = table.getTableFormat();

						// int rowLast = 0;// 最后 一次增加的行.没有就为0
						// int columnLast = 0;// 最后一次操作的列.没有就为0
						// 行数
						int rows = tableFormat.getRow();
						// 列数
						int columns = tableFormat.getColumn();
						// 默认行高（单位：0.1毫米）
						double defaultRowHeightFloat = tableFormat.getDefaultRowHeight();
						// 默认列宽（单位：0.1毫米）
						double defaultColumnWidthFloat = tableFormat.getDefaultColumnWidth();
						// 单元格左边距（单位：0。1毫米）
						float cellLeftSpaceFloat = tableFormat.getCellLeftSpace();
						// 单元格右边距（单位：0。1毫米）
						float cellRightSpaceFloat = tableFormat.getCellRightSpace();
						// 对应“上下左右横列”边框，默认设置"111111"
						String frameString = "" + getValueWithDefault(tableFormat.getFrame(), 111111);

						if (StringUtils.isEmpty(frameString) || frameString.length() != 6) {
							logger.error("边框frame格式不正确;frame=" + frameString);
							frameString = "111111";// 设置默认的边框格式
						}

						double[] dArrHeight = new double[rows];
						double[] dArrLength = new double[columns];

						CellInfo[][] oArrRowCol = new CellInfo[rows][columns];
						double dCurTop = 0, dCurLeft = 0;

						// 设置缺省行高
						for (int i = 0; i < rows; i++) {
							dArrHeight[i] = defaultRowHeightFloat;
						}
						// 行定义
						List<RowDefine> rowDefineList = table.getRowDefineList();
						// 行高定义
						for (int i = 0; rowDefineList != null && i < rowDefineList.size(); i++) {
							RowDefine rowDefine = rowDefineList.get(i);
							dArrHeight[rowDefine.getRowId() - 1] = rowDefine.getRowHeight();
						}

						// 缺省列宽
						for (int i = 0; i < columns; i++) {
							dArrLength[i] = defaultColumnWidthFloat;
						}
						// 列定义
						List<ColumnDefine> columnDefineList = table.getColumnDefineList();
						// 列宽定义
						for (int i = 0; columnDefineList != null && i < columnDefineList.size(); i++) {
							ColumnDefine columnDefine = columnDefineList.get(i);
							dArrLength[columnDefine.getColumnId() - 1] = columnDefine.getColumnWidth();
						}

						int iTopFrame, iBottomFrame, iLeftFrame, iRightFrame, iHFrame, iVFrame;
						// 上边框
						iTopFrame = NumberUtils.toInt(frameString.substring(0, 1));
						// 下边框
						iBottomFrame = NumberUtils.toInt(frameString.substring(1, 2));
						// 左边框
						iLeftFrame = NumberUtils.toInt(frameString.substring(2, 3));
						// 右边框
						iRightFrame = NumberUtils.toInt(frameString.substring(3, 4));
						// 横边框
						iHFrame = NumberUtils.toInt(frameString.substring(4, 5));
						// 竖边框
						iVFrame = NumberUtils.toInt(frameString.substring(5, 6));

						// 减去边框的高度
						for (int i = 0; i < rows; i++) {
							if (i == 0) {
								dArrHeight[i] = Math.round(dArrHeight[i] - calcFrameValue(iTopFrame));
							}
							else if (i == (rows - 1)) {
								dArrHeight[i] = Math.round(dArrHeight[i] - calcFrameValue(iBottomFrame));
							}
							else {
								dArrHeight[i] = Math.round(dArrHeight[i] - calcFrameValue(iHFrame));
							}
						}

						// 减去边框的宽度
						for (int i = 0; i < columns; i++) {
							if (i == 0) {
								dArrLength[i] = Math.round(dArrLength[i] - calcFrameValue(iLeftFrame));
							}
							else if (i == (columns - 1)) {
								dArrLength[i] = Math.round(dArrLength[i] - calcFrameValue(iRightFrame));
							}
							else {
								dArrLength[i] = Math.round(dArrLength[i] - calcFrameValue(iVFrame));
							}
						}

						htmlSb.append(" <div style=\"position: absolute;");
						htmlSb.append(" top: " + (iAbsolute_y + locationYFloat / 10) + "mm;");
						htmlSb.append(" left: " + (locationXFloat / 10) + "mm;");
						htmlSb.append(" width: " + (blockLengthFloat / 10) + "mm;");

						if (blockHeighFloat > 0) {
							htmlSb.append(" height: " + (blockHeighFloat / 10) + "mm;");
						}

						htmlSb.append(" border-top: " + calcFrameBANG(iTopFrame));
						htmlSb.append(" border-bottom: " + calcFrameBANG(iBottomFrame));
						htmlSb.append(" border-Left: " + calcFrameBANG(iLeftFrame));
						htmlSb.append(" border-right: " + calcFrameBANG(iRightFrame));
						htmlSb.append(" overflow-x:hidden;overflow-y:hidden;\">");

						List<CellInfo> cellInfoList = table.getCellInfoList();// 单元格
						outCellInfo: for (CellInfo cellInfo : cellInfoList) {
							int cellRowId = cellInfo.getCellRowId();// 行号
							int cellColumnId = cellInfo.getCellColumnId();// 列号
							if (cellRowId == 0) {
								logger.error("获取到的当前行号不正确;cell_row_id=" + cellInfo.getCellRowId());
								continue outCellInfo;
							}

							if (cellColumnId == 0) {
								logger.error("获取到的当前列号不正确;cell_column_id=" + cellInfo.getCellColumnId());
								continue outCellInfo;
							}

							cellRowId = cellRowId - 1;
							cellColumnId = cellColumnId - 1;
							oArrRowCol[cellRowId][cellColumnId] = cellInfo;
						}

						htmlSb.append("<table cellspacing=\"0\" cellpadding=\"0\">");

						dCurTop = calcFrameValue(iTopFrame);

						for (int i = 0; i < rows; i++) {
							htmlSb.append("<tr>");
							for (int j = 0; j < columns; j++) {
								CellInfo cellInfo = oArrRowCol[i][j];
								if (j == 0) {// 左边
									dCurLeft = calcFrameValue(iLeftFrame);
								}

								htmlSb.append("<td style=\"");
								htmlSb.append(" width: " + (float) dArrLength[j] / 10 + "mm;");
								htmlSb.append(" height: " + (float) dArrHeight[i] / 10 + "mm;");
								htmlSb.append(" border-top: 0pt; border-Left: 0pt;");

								if (i < (rows - 1)) {// 不是最后一行
									htmlSb.append(" border-bottom: "
											+ calcFrameBANG(iHFrame));
								}
								if (j < (columns - 1)) {// 不是最后一列
									htmlSb.append(" border-right: "
											+ calcFrameBANG(iVFrame));
								}
								htmlSb.append("\"");

								if (cellInfo != null) {
									AlignmentInfo alignmentInfo = cellInfo.getAlignmentInfo();// 对齐方式
									// 垂直上下对齐（1=上、2=中、3=下），基于上下边距之间，默认-1
									int alignmentVertical = alignmentInfo.getAlignmentVertical();
									// 表格底色
									String colourBackgroundColorString = cellInfo.getBottomColour();

									if (alignmentVertical != -1) {// 存在垂直上下对齐
										// 垂直对齐方式
										htmlSb.append(" valign=\"");
										if (alignmentVertical == 1) {
											htmlSb.append("top");
										}
										else if (alignmentVertical == 2) {
											htmlSb.append("middle");
										}
										else if (alignmentVertical == 3) {
											htmlSb.append("bottom");
										}
										htmlSb.append("\"");
									}

									if (StringUtils.isNotEmpty(colourBackgroundColorString)) {// 存在表格底色
										htmlSb.append(" bgcolor=\"" + colourBackgroundColorString + "\"");
									}

								}
								htmlSb.append(">");

								htmlSb.append("<div style=\"margin-left: " + (cellLeftSpaceFloat / 10) + "mm;");
								htmlSb.append(" margin-right: " + (cellRightSpaceFloat / 10) + "mm;");

								if (cellInfo != null) {
									FontInfo fontInfo = cellInfo.getFontInfo();// 字体信息
									// 字体（1=宋体；2=黑体）
									int fontType = fontInfo.getFontType();
									// 字形（0=常规、1=加粗、2=倾斜、3=加粗倾斜）
									int fontStyle = fontInfo.getFontStyle();
									// 字号（单位：磅），默认9
									String fontSize = fontInfo.getFontSize();
									// 下划线（1=有下划线）
									int fontUnderline = fontInfo.getFontUnderline();
									// 字体颜色（#000000-#FFFFFF）
									String fontColourString = fontInfo.getFontColour();
									// 缩进（单位：空格）
									int indent = fontInfo.getIndent();
									// 水平左右对齐（1=左、2=中、3=右），基于左右边距之间，默认-1
									int alignmentHorizontal = cellInfo.getAlignmentInfo().getAlignmentHorizontal();
									// 表格内容
									String cellContentString = cellInfo.getCellContent();

									// 字体
									htmlSb.append("font: ");
									if (fontStyle != -1) {
										if (fontStyle == 1) {// 加粗
											htmlSb.append("bold");
										}
										else if (fontStyle == 2) {// 倾斜
											htmlSb.append("Italic");
										}
										else if (fontStyle == 3) {// 加粗倾斜
											htmlSb.append("Bold Italic");
										}
									}

									// 字体大小
									htmlSb.append(" " + fontSize + "pt ");

									// 字体
									if (fontType != -1) {
										if (fontType == 1) {// 宋体
											htmlSb.append("宋体;");
										}
										else if (fontType == 2) {// 黑体
											htmlSb.append("黑体;");
										}
									}

									// 下划线
									if (fontUnderline == 1) {
										htmlSb.append(" text-decoration: underline;");
									}

									// 字体颜色
									htmlSb.append(" color: " + fontColourString
											+ ";");

									// 水平对齐方式
									if (alignmentHorizontal != -1) {
										htmlSb.append(" text-align: ");
										if (alignmentHorizontal == 1) {// 左对齐
											htmlSb.append("left");
										}
										else if (alignmentHorizontal == 2) {// 中对齐
											htmlSb.append("center");
										}
										else if (alignmentHorizontal == 3) {// 右对齐
											htmlSb.append("right");
										}
										htmlSb.append(";");
									}

									htmlSb.append("\">");

									// 缩进+单元格内容
									for (int k = 0; k < indent; k++) {
										htmlSb.append("&nbsp;");
									}

									if (StringUtils.isEmpty(cellContentString)) {
										htmlSb.append("&nbsp;");
									}
									else {
										htmlSb.append(cellContentString);
									}

								}
								else {
									htmlSb.append("\">&nbsp;");
								}

								htmlSb.append("</div>\n");
								htmlSb.append("</td>\n");
								dCurLeft = dCurLeft + dArrLength[j];
							}

							htmlSb.append("</tr>\n");
							dCurTop = dCurTop + dArrHeight[i];
						}

						htmlSb.append("</table>\n");
						htmlSb.append("</div>\n");

					}

					if (blockType == 2) {// 图片
						//logger.info("存在图片");
						PictureInfo pictureInfo = bolckBlockInfo.getPictureInfo();
						String fileNameString = "" + pictureInfo.getFileName();// 文件名
						String displayString = "" + pictureInfo.getDisplay();// 显示方式

						String filePath = getPictureFile(fileNameString, picturePath);// 获取图片路径名

						if (StringUtils.isNotEmpty(filePath)) {
							htmlSb.append("<div style=\"position: absolute;");
							htmlSb.append(" top: " + (iAbsolute_y + locationYFloat / 10) + "mm;");
							htmlSb.append(" left: " + (locationXFloat / 10) + "mm;");
							htmlSb.append(" width: " + (blockLengthFloat / 10) + "mm;");
							htmlSb.append(" height: " + (blockHeighFloat / 10) + "mm;");
							htmlSb.append(" overflow-x:hidden;overflow-y:hidden;\">");

							htmlSb.append("<table cellspacing=\"0\" height=\"100%\" width=\"100%\"><td style=\"align: center; valign: middle;\">");

							//logger.info("filePath:" + filePath);
							if ("0".equals(displayString)) {// 自适应
								htmlSb
										.append("<img src=\""
												+ filePath
												+ "\" height=\"100%\" width=\"100%\" />");
							}
							else if ("1".equals(displayString)) {// 居中
								htmlSb.append("<img src=\"" + filePath
										+ "\"  />");
							}

							htmlSb.append("</td>\n</table>\n");
							htmlSb.append("</div>\n");

						}else{
							logger.info("不存在图片.文件路径path:" + filePath);
						}

					}

					if (blockType == 3) {// 消费柱状图
						//logger.info("存在柱形图");
						HistogramInfo histogramInfo = bolckBlockInfo.getHistogramInfo();
						String ifDisplaValueString = "" + histogramInfo.getIfDisplaValue();// 是否显示数值
						String columnWidthString = "" + histogramInfo.getGraphColumnWidth();// 柱子的宽度
						List<HistogramDataInfo> histogramDataInfoList = histogramInfo.getHistogramDataInfoList();// 消费数据

						if (histogramDataInfoList != null && histogramDataInfoList.size() > 0) {// 存在消费数据值
							htmlSb.append("<div style=\"position: absolute;");
							htmlSb.append(" top: " + (iAbsolute_y + locationYFloat / 10) + "mm;");
							htmlSb.append(" left: " + (locationXFloat / 10) + "mm;");
							htmlSb.append(" width: " + (blockLengthFloat / 10) + "mm;");
							htmlSb.append(" height: " + (blockHeighFloat / 10) + "mm;");
							htmlSb.append(" overflow-x:hidden;overflow-y:hidden;valign=bottom\">");

							htmlSb.append("<table width=\"100%\" height=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\"  align=\"center\">");

							htmlSb.append("<tr>");
							htmlSb.append("<td>\n<script type=\"text/javascript\"> \n");

//										htmlSb.append("var ar,space,width=" + (int) Math.round(blockLengthFloat / ONEBANGMM * 4 / 3));
//										htmlSb.append(",height=" + (int) Math.round(blockHeighFloat / ONEBANGMM * 4 / 3) + "\n");
//										htmlSb.append("ar = new Array();\n");
							htmlSb.append("var ar,space,width=380,height=170;\n");
							htmlSb.append("ar = new Array();\n");

							HistogramDataInfo histogramDataInfo = null;
							for (int i = 0; i < histogramDataInfoList.size(); i++) {
								histogramDataInfo = histogramDataInfoList.get(i);
								htmlSb
										.append("ar["+ i+ "]=\""+ histogramDataInfo.getxValue()
												+ "月"
												+ "|"
												+ histogramDataInfo.getyValue()
												+ "|"
												+ histogramDataInfo.getGraphColour()
												+ "|"
												+ getYByDisplay(ifDisplaValueString,histogramDataInfo)
												+ "\"\n");
							}

							htmlSb.append("space=20;\n");
							htmlSb.append("histogram(ar,space,width,height);\n");
							htmlSb.append("</script>\n</td>\n");
							htmlSb.append("   </tr>\n");
							htmlSb.append("</table>\n</div>\n");

						}

					}

				}
				iAbsolute_y = iAbsolute_y + paperHeightInt;
			}
			
		}
		catch (Exception e) {
			logger.error(e);
			return "";
		}

		return htmlSb.toString();
	}
	
	
	private int getValueWithDefault(int value, int defaultVal) {
		return value == 0 ? defaultVal : value;
	}
	
	private String getValueWithDefault(String value, String defaultVal) {
		return value == null || value.length() == 0 ? defaultVal : value;
	}

	public double calcFrameValue(int pFrameValue) {
		double result = 0;
		if (pFrameValue == 2 || pFrameValue == 4) {// 普通.虚线
			result = ONEBANGMM / 2;
		}

		if (pFrameValue == 3) {// 加粗
			result = ONEBANGMM;
		}

		return result;
	}

	public String calcFrameBANG(int pFrameValue) {
		String result = "0";

		if (pFrameValue == 2 || pFrameValue == 4) {// 普通.虚线
			result = "0.5";
		}

		if (pFrameValue == 3) {// 加粗
			result = "1";
		}

		result = result + "pt ";

		if (pFrameValue == 4) {
			result = result + "dashed";
		}
		else {
			result = result + "solid";
		}
		result = result + " #000000;";
		return result;
	}

	/**
	 * 根据图片的文件名,返回该图片的路径
	 * 
	 * @param Value
	 * @return
	 */
	private String getPictureFile(String fileName, String path) {// 默认为0
		String picFile = "";
		
		picFile = path + fileName + ".png";
		return picFile;
	}

	private String getYByDisplay(String displayValue, HistogramDataInfo histogramDataInfo) {
		if ("1".equals(displayValue)) {// 显示
			return "" + histogramDataInfo.getyValue();
		}
		else {
			return "";
		}
	}
